import { useState, useEffect } from "react";

const dummyEvents = [
  {
    id: 1,
    title: "Diwali Celebration",
    description: "Join us for a night of lights, music, and food!",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6jgkrqVz0ecIGlVeHKkfLTsw8ntQGkc3tKw&s", // âœ… Use correct relative path from public/
  },
  {
    id: 2,
    title: "Holi Fest",
    description: "Celebrate the festival of colors with friends!",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 3,
    title: "Independence Day",
    description: "Flag hoisting and cultural programs.",
    image: "data:image/jpeg;base64,",
  },
];

const Events = () => {
  const [events, setEvents] = useState([]);
  const [rsvpStatus, setRsvpStatus] = useState({});

  useEffect(() => {
    setEvents(dummyEvents);
  }, []);

  const handleRSVP = (eventId) => {
    setRsvpStatus((prev) => ({ ...prev, [eventId]: true }));
    alert("You have successfully RSVPâ€™d for the event!");
  };

  return (
    <div className="events-container">
      <h2>Upcoming Events</h2>
      <div className="event-grid">
        {events.map((event) => (
          <div key={event.id} className="event-card">
            <img src={event.image} alt={event.title} style={{ width: "100%", height: "auto" }} />
            <h3>{event.title}</h3>
            <p>{event.description}</p>
            <button
              onClick={() => handleRSVP(event.id)}
              disabled={rsvpStatus[event.id]}
              style={{
                padding: "10px 20px",
                backgroundColor: rsvpStatus[event.id] ? "gray" : "#1e3a8a",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: rsvpStatus[event.id] ? "not-allowed" : "pointer",
              }}
            >
              {rsvpStatus[event.id] ? "RSVPâ€™d" : "RSVP"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
