import { useState, useEffect } from "react";

const dummyNews = [
  {
    id: 1,
    title: "Annual Fest Announced!",
    content: "Our much-awaited Annual Fest will be held on October 10th. Stay tuned!",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSZ9L5qqnDykHfUJJvxbqLxTpK-H7kMAo3scQ&s",
  },
  {
    id: 2,
    title: "New Cafeteria Opening",
    content: "A modern cafeteria with Wi-Fi and snacks is now open near Block B.",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 3,
    title: "Library Extended Hours",
    content: "The Central Library will now be open till 10 PM during exams.",
    image: "data:image/jpeg;base64,",
  },
];

const News = () => {
  const [newsList, setNewsList] = useState([]);

  useEffect(() => {
    // Simulate fetching from API
    setNewsList(dummyNews);
  }, []);

  return (
    <div className="news-container">
      <h2>Campus News</h2>
      <div className="news-grid">
        {newsList.map((news) => (
          <div key={news.id} className="news-card">
            <img src={news.image} alt={news.title} />
            <h3>{news.title}</h3>
            <p>{news.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
